mapper.update:
update loan_bank set editor_id = #{editorId}, edit_time = #{editTime},
code = #{code}, name = #{name}, image = #{image}, show_state = #{showState}, serial_number = #{serialNumber}
where id = #{id}

mapper.select:
select 
from loan_bank
where status = true and flag = true
 and show_state = #{showState} 
 and code = #{code} 


    and name like #{nameLike}

 and ((id <= #{exceptId - 1}) or (id >= #{exceptId + 1})) 
order by serial_number asc, id desc
